/**
 * Doctrine_Record_Listener class used to listen for and hook into events.
 *
 * This is the concrete listener class for ticket entities.
 */
class KnowledgeBase_Model_TicketListener extends KnowledgeBase_Model_Base_TicketListener
{
    /**
     * Post-Process the data after a hydrate operation.
     *
     * @param Doctrine_Event $event Event object
     *
     * @return void.
     */
    public function postHydrate(Doctrine_Event $event)
    {
        parent::postHydrate($event);
        $event->data = KnowledgeBase_Util_Controller::postProcessTicket($event->data);
    }
}
